import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import {IQuestion, IQuestionnaireResponse} from "../models/questionnaire";
import {BehaviorSubject, map, switchMap, tap} from "rxjs";

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html'
})
export class FetchDataComponent {
  public questionsList: IQuestionnaireResponse | undefined = undefined;

  public selectOptions: string[] =  ['Option 1', 'Option 2', 'Option 3' ];
  public data$: BehaviorSubject<IQuestion[]> = new BehaviorSubject<IQuestion[]>([])

  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string) {
    http.get<IQuestionnaireResponse>(baseUrl + 'weatherforecast').subscribe(result => {
      this.data$.next(result.data)
    }, error => console.error(error));

  }

  public onAnswer(question: IQuestion): void {
    const newQuestion: IQuestion = {...question, answerValue: "hello"};
    this.http.post(this.baseUrl + 'weatherforecast',  newQuestion).pipe(
      switchMap(() => this.data$),
      map((qarr: IQuestion[]): IQuestion[] => {
        return qarr.map(e => e.questionId === newQuestion.questionId ? {...e, answerValue: "hello"} : e)
      }),
      tap((moddedArr: IQuestion[]) =>  {
        this.data$.next(moddedArr)
      })
    ).subscribe()
  }

  onClear() {
    this.http.delete(this.baseUrl + 'weatherforecast' ).subscribe()
  }
}

interface WeatherForecast {
  date: string;
  temperatureC: number;
  temperatureF: number;
  summary: string;
}
<input
  (click)="onClear()"
  style="display: block" type="button" value="Clear db">
<h1 id="tableLabel">Questionnaire</h1>

<p>This component demonstrates fetching data from the server.</p>

<p *ngIf="!questionsList"><em>Loading...</em></p>

<div *ngIf="data$ | async as data">
  <ng-container *ngFor="let question of data">
    {{question | json}}
    <h4>Q#: {{question.questionId}}</h4>

    <h4>{{question.questionText}}?</h4>
    <ng-container *ngIf="question.answerValue">
      <h4>Your Answer is: {{question.answerValue}}</h4>
    </ng-container>

    <ng-container *ngIf="question.answerValue == null">

      <ng-container *ngIf="question.answerType === 0">
        <input type="text">
      </ng-container>

      <ng-container *ngIf="question.answerType === 1">
        <input type="number">
      </ng-container>

      <ng-container *ngIf="question.answerType === 2">
        <select name="multiple" id="select">
          <option *ngFor="let option of selectOptions" value="{{option}}">{{option}}</option>
        </select>
      </ng-container>

      <input
        [disabled]="question.answerValue != null"
        (click)="onAnswer(question)"
        style="display: block" type="button" value="Answer">

    </ng-container>


    <br>

    <br>
    <br>

  </ng-container>
</div>

